import React, { Component } from 'react'
import Table from './Table'


class App extends Component {
  state = {
    characters: [
      {
        name: 'Charlie',
      }
    ]
  }
}

removeCharacter = index => {
  const { characters } = this.state

  this.setState({
    characters: characters.filter((characters, i) => {
      return i !== index
    }),
  })
}

